import axios from 'axios';

const api = () => { 

  const searchUser = (user) => {

    axios.get(`http://localhost:3001/search?user=${user}`)
        .then(res => {
          return res;
        });

  }

  return {
    searchUser
  };
}


export const ApiService = new api();
