import React from 'react';
export default class UserInfo extends React.Component {

  state = {
    shouldHide: true
  }
  componentWillReceiveProps(props){

    console.log(props.hideUser);
    this.setState({shouldHide: props.hideUser});
  }
  handleChange(event) {
    this.setState({userName: event.target.value});
  }

  render() {
    return (
    <section className={this.state.shouldHide ? 'hidden' : ''}>
      <img src = "https://avatars.githubusercontent.com/u/25516236?v=4" alt = "User avatar"/>
      <span> User name: </span>
      <span> User profile </span>
      <span> Number of projects: </span> 
    </section>
    );
  }
}
