import React from 'react';
import './UserInfo.css';

export default class UserInfo extends React.Component {

  render() {
    let hidden = this.props.hideUser ? 'hidden' : ''
    let classes = `user-info ${hidden}`;
    return (

      <section className={classes}>
      <p>"props" {this.props.hideUser} </p>
      <img src = "https://avatars.githubusercontent.com/u/25516236?v=4" alt = "User avatar"/>
      <span> User name: {this.props.info.userName}</span>
      <span> User profile: {this.props.info.userProfile} </span>
      <span> Number of projects: {this.props.info.userNumberProjects} </span> 
    </section>

    );
  }
}
