import './App.css';
import React from 'react';
import './reset.css';
import SearchBar from './components/Search/Search.js';
import UserInfo from './components/UserInfo/UserInfo.js';
import Repo from './components/Repo/Repo.js';


class Container extends React.Component {
  constructor(props) {
      super(props);
      this.state = {hideUser: true};
  }

  updateList (data) {
    this.setState({data: data});
    console.log("atualizando!")
  }
  render() {
      return (
        <div className="App">
        <SearchBar updateList = { this.updateList }/>
        <UserInfo hideUser = { this.state.hideUser }/>
        <Repo repoList = { this.state.repoList } />
      </div>
      );
  }
}

function App() {
  return <Container />
}

export default App;
